import { useState, useEffect, Fragment } from "react";
import DatePicker, { registerLocale } from "react-datepicker";
import th from "date-fns/locale/th";
import { Button, Container, Navbar, Row, Col, Table } from "react-bootstrap";
import Pagination from "react-js-pagination";
import Select from "react-select";
import BlockUi from "react-block-ui";
import axios from "axios";
registerLocale("th", th);

const http = axios.create({
  // baseURL: process.env.api,
  baseURL: "http://my-json-server.typicode.com/guitarjaku/react-code-test",
  headers: {
    "Content-type": "application/json",
  },
});

const App = () => {
  const [date, setDate] = useState(new Date());
  const [time, setTime] = useState(new Date());
  const [sortFirstName, setSortFirstName] = useState(false);
  const [queryStatus, setQuery] = useState({
    first: "",
    last: "",
    gender: "",
  });
  const [activePage, setActivePage] = useState(1);
  const [blocking, handleBlocking] = useState(true);
  const [dataTable, setDataTable] = useState([]);

  const options = [
    { value: "chocolate", label: "Chocolate" },
    { value: "strawberry", label: "Strawberry" },
    { value: "vanilla", label: "Vanilla" },
  ];

  const getEmployees = async () => {
    await http
      .get(
        `/employee${queryStatus.first + queryStatus.last + queryStatus.gender}`
      )
      .then((res) => {
        setDataTable(res.data);
        handleBlocking(false);
      })
      .catch((err) => console.log(err));
  };

  const hanedleSort = () => {
    console.log("aaa");

    setSortFirstName(!sortFirstName);

    // if (sortFirstName) {
    setQuery({ ...queryStatus, first: "?first_name&_order=asc" });
    // } else {
    //   setQuery({ ...queryStatus, first: "" });
    // }
    getEmployees();
    // console.log("queryStatus", queryStatus);
  };

  useEffect(() => {
    getEmployees();
  }, []);

  return (
    <Fragment>
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="#home">
          <img
            alt=""
            src={"/logo192.png"}
            width="30"
            height="30"
            className="d-inline-block align-top mr-2"
          />
          React Bootstrap
        </Navbar.Brand>
      </Navbar>
      {/* <Button onClick={() => handleBlocking(!blocking)} color="primary">
        Toggle Block
      </Button> */}
      <BlockUi tag="div" blocking={blocking}>
        <Container fluid className="mb-4">
          <Row>
            <Col xs={12} sm={6}>
              <div className="w-100 text-sm-right text-center mt-2">
                <span>Date : </span>
                <DatePicker
                  locale="th"
                  selected={date}
                  onChange={(date) => setDate(date)}
                />
              </div>
            </Col>
            <Col xs={12} sm={6}>
              <div className="w-100 text-sm-left text-center mt-2">
                <span>Time : </span>
                <DatePicker
                  selected={time}
                  onChange={(date) => setTime(date)}
                  showTimeInput
                  showTimeSelectOnly
                  timeCaption="Time"
                  dateFormat="HH:mm"
                />
              </div>
            </Col>
          </Row>
          <Row className="mt-2">
            <div className="d-flex w-100 px-3">
              <div className="form-group mr-auto">
                <select className="form-control" id="item">
                  <option>10</option>
                  <option>20</option>
                  <option>30</option>
                  <option>50</option>
                  <option>100</option>
                </select>
              </div>

              <div className="text-right">
                <Pagination
                  hideDisabled
                  itemClass="page-item"
                  linkClass="page-link"
                  // firstPageText="First"
                  // lastPageText="Last"
                  pageRangeDisplayed={3}
                  activePage={activePage}
                  itemsCountPerPage={10}
                  totalItemsCount={100}
                  onChange={(pageNumber) => {
                    setActivePage(pageNumber);
                  }}
                />
              </div>
            </div>
            <div onClick={() => hanedleSort()}>click</div>
            <Col xs={12}>
              <table>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>
                      <button onClick={() => hanedleSort()}>First Name</button>
                      <img
                        src={
                          "icons/sort-arrows-couple-pointing-up-and-down.png"
                        }
                      />
                    </th>
                    <th>
                      Last Name
                      <img
                        src={
                          "icons/sort-arrows-couple-pointing-up-and-down.png"
                        }
                      />
                    </th>
                    {/* <th>Email</th> */}
                    <th>
                      Gender
                      <img
                        src={
                          "icons/sort-arrows-couple-pointing-up-and-down.png"
                        }
                      />
                    </th>
                    {/* <th>IP Address</th> */}
                  </tr>
                </thead>
                <tbody>
                  {dataTable &&
                    dataTable.map((row, i) => (
                      <tr key={i}>
                        <td>{i + 1}</td>
                        <td>{row.first_name}</td>
                        <td>{row.last_name}</td>
                        {/* <td>{row.email}</td> */}
                        <td>{row.gender}</td>
                        {/* <td>{row.ip_address}</td> */}
                      </tr>
                    ))}
                </tbody>
              </table>
              <pre>{dataTable}</pre>
            </Col>
            <Col xs={12}>{/* <Select options={options} /> */}</Col>
          </Row>
        </Container>
      </BlockUi>
    </Fragment>
  );
};

export default App;
